import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

export default function Page2() {
  const [popped, setPopped] = useState(false);

  const handlePress = () => {
    setPopped(!popped);
  };

  return (
    <View style={styles.container}>
      <View style={styles.sunlightContent}>
        <View style={styles.sunlightBox}>
          <View style={[styles.sunlightBoxSpan, popped && styles.poppedSunlightBoxSpan]} />
          <View style={[styles.sunlightBoxSpan, popped && styles.poppedSunlightBoxSpan]} />
          <View style={[styles.sunlightBoxSpan, popped && styles.poppedSunlightBoxSpan]} />
          <TouchableOpacity onPress={handlePress} style={styles.centerCircle} activeOpacity={0.8}>
            <View style={[styles.circle, popped && styles.poppedCircle]} />
          </TouchableOpacity>
        </View>
      </View>
      <View style={styles.footer}>
        <Text style={styles.energy}>This ball has energy.</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1e1919',
    justifyContent: 'center',
    alignItems: 'center',
  },
  sunlightContent: {
    position: 'relative',
    width: 200,
    height: 200,
    overflow: 'hidden',
  },
  sunlightBox: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  sunlightBoxSpan: {
    width: 50,
    height: 50,
    backgroundColor: '#f8a037',
    borderRadius: 25,
  },
  poppedSunlightBoxSpan: {
    backgroundColor: 'yellow',
  },
  centerCircle: {
    position: 'absolute',
    width: 200,
    height: 200,
    justifyContent: 'center',
    alignItems: 'center',
  },
  circle: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: 'red',
  },
  poppedCircle: {
    width: 100,
    height: 100,
    borderRadius: 50,
  },
  footer: {
    position: 'absolute',
    bottom: 20,
  },
  energy: {
    color: 'yellow',
  },
});
