import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Animated, Easing, TouchableOpacity } from 'react-native';

const Page2 = () => {
  const [rotation] = useState(new Animated.Value(0));
  const [brightness] = useState(new Animated.Value(1));
  const [scale] = useState(new Animated.Value(1));

  useEffect(() => {
    const animation = Animated.loop(
      Animated.timing(rotation, {
        toValue: 1,
        duration: 20000,
        easing: Easing.linear,
        useNativeDriver: true,
      })
    );
    animation.start();
    return () => animation.stop(); // Clean up animation on unmount
  }, [rotation]);

  const rotateInterpolation = rotation.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  const handleImageClick = () => {
    const popOutDuration = 200;
    const returnDuration = 200;
    const totalDuration = popOutDuration + returnDuration;
    const brightnessIncrease = Animated.timing(brightness, {
      toValue: 2,
      duration: popOutDuration,
      useNativeDriver: true,
    });
    const brightnessReturn = Animated.timing(brightness, {
      toValue: 1,
      duration: returnDuration,
      useNativeDriver: true,
    });
    const scaleIncrease = Animated.timing(scale, {
      toValue: 1.2,
      duration: popOutDuration,
      useNativeDriver: true,
    });
    const scaleReturn = Animated.timing(scale, {
      toValue: 1,
      duration: returnDuration,
      useNativeDriver: true,
    });
    const trianglesMoveUp = Animated.timing(trianglesPosition, {  // Assuming you have a state for trianglesPosition
      toValue: 0,
      duration: popOutDuration,
      useNativeDriver: true,
    });
    const trianglesMoveDown = Animated.timing(trianglesPosition, {
      toValue: 30,
      duration: returnDuration,
      useNativeDriver: true,
    });
  
    Animated.sequence([
      Animated.parallel([brightnessIncrease, scaleIncrease, trianglesMoveUp]),
      Animated.delay(5000), // Delay for 5 seconds
      Animated.parallel([brightnessReturn, scaleReturn, trianglesMoveDown]),
    ]).start();
  }};
  
const styles = StyleSheet.create({
  sunlightContent: {
    flex: 1,
    backgroundColor: '#1e1919',
    justifyContent: 'center',
    alignItems: 'center',
  },

  ball: {
    height: 150,
    width: 150,
    borderRadius: 75,
    borderWidth: 1,
    zIndex: 1,
    position: 'absolute',
  },
  footer: {
    position: 'absolute',
    bottom: 20,
    fontSize: 20,
    color: 'white',
  },
  energy: {
    color: 'yellow',
  },
  triangle: {
    position: 'absolute',
    width: 0,
    height: 0,
    borderTopWidth: 60,
    borderRightWidth: 60,
    borderBottomWidth: 0,
    borderLeftWidth: 0,
    borderTopColor: 'yellow',
    transformOrigin: 'center center',
  },
});

export default Page2;
