import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import {auth,db} from './firebase'

const PinEntryScreen = ({ navigation, route }) => {
  const [pin, setPin] = useState('');
  const [userPin, setUserPin] = useState('');

  useEffect(() => {
    // Retrieve the user's PIN from the database using the passed userEmail
    const userEmail = route.params.userEmail;
    const userRef = firebase.firestore().collection('users').doc(userEmail);
    userRef.get().then((doc) => {
      if (doc.exists) {
        const userData = doc.data();
        setUserPin(userData.pin);
      } else {
        console.log('User not found');
      }
    });
  }, []);

  const handlePinChange = (enteredPin) => {
    setPin(enteredPin);
  };

  const handlePinSubmit = () => {
    if (pin === userPin) {
      console.log('PIN entered correctly');
      // Navigate back or to another screen after PIN verification
      navigation.goBack();
    } else {
      console.log('Incorrect PIN');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.prompt}>Enter 4-Digit PIN:</Text>
      <TextInput
        style={styles.input}
        value={pin}
        onChangeText={handlePinChange}
        keyboardType="numeric"
        maxLength={4}
      />
      <Button title="Submit" onPress={handlePinSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  prompt: {
    fontSize: 18,
    marginBottom: 20,
  },
  input: {
    width: '80%',
    padding: 10,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 20,
    textAlign: 'center',
    fontSize: 24,
  },
});

export default PinEntryScreen;
