import React, { useState } from 'react';
import { View, TouchableOpacity, Text, TextInput, StyleSheet, Image, KeyboardAvoidingView, Alert } from 'react-native';
import { Itim_400Regular } from '@expo-google-fonts/itim';
import { useFonts } from 'expo-font';
import phyDoodleShapes from '../assets/BgImage/doodle.png';
import goBackButton from '../assets/menuImages/gobackIcon.png';
import ParentUI from '../ParentUI/ParentUI';
import AiBook from '../assets/booksImages/AIBook.png';
import ConfirmPurchaseButton from '../assets/buyButtons/ConfirmPurchaseButton.png';
import PurchaseSuccessful from '../purchaseSuccessful';

const Payment = () => {
  const [showParentUI, setShowParentUI] = useState(false);
  const [purchaseComplete, setPurchaseComplete] = useState(false);
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [name, setName] = useState('');
  const [cardNumberError, setCardNumberError] = useState('');
  const [expiryDateError, setExpiryDateError] = useState('');
  const [cvvError, setCvvError] = useState('');
  const [nameError, setNameError] = useState('');

  const [fontsLoaded] = useFonts({
    Itim_400Regular,
  });

  if (!fontsLoaded) {
    return <Text>Loading...</Text>;
  }

  const handleGoBack = () => {
    setShowParentUI(true);
  };

  const handleConfirmPurchase = async () => {
    let isValid = true;

    // Clear previous error messages
    setCardNumberError('');
    setExpiryDateError('');
    setCvvError('');
    setNameError('');

    // Validate card number
    if (!cardNumber) {
      setCardNumberError('Card number is required');
      isValid = false;
    }

    // Validate expiry date
    const expiryDateRegex = /^(0[1-9]|1[0-2])\/\d{2}$/;
    if (!expiryDate) {
      setExpiryDateError('Expiry date is required');
      isValid = false;
    } else if (!expiryDateRegex.test(expiryDate)) {
      setExpiryDateError('Invalid expiry date format');
      isValid = false;
    }

    // Validate CVV
    if (!cvv) {
      setCvvError('CVV is required');
      isValid = false;
    } else if (!/^[0-9]{3,4}$/.test(cvv)) {
      setCvvError('Invalid CVV');
      isValid = false;
    }

    // Validate name
    if (!name) {
      setNameError('Name is required');
      isValid = false;
    } else if (!/^[A-Za-z\s]+$/.test(name)) {
      setNameError('Invalid name');
      isValid = false;
    }

    if (!isValid) {
      return;
    }

    try {
      const response = await fetch('http://localhost:3000/saveDetails', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ cardNumber, expiryDate, cvv, name }),
      });
      const data = await response.json();
      if (response.status === 200) {
        setPurchaseComplete(true);
      } else {
        Alert.alert('Error', data.message);
      }
    } catch (error) {
      Alert.alert('Error', 'Failed to process payment');
    }
  };

  if (showParentUI) {
    return <ParentUI handleGoBack={() => setShowParentUI(false)} />;
  }

  if (purchaseComplete) {
    return <PurchaseSuccessful />;
  }

  return (
    <KeyboardAvoidingView style={styles.container} behavior='padding'>
      <Text style={styles.header}>The Book Store</Text>
      <View>
        <Image source={phyDoodleShapes} style={styles.backgroundImage} />
        <Text style={styles.AiBookTitle}>Artificial Intelligence for Babies</Text>
        <View style={styles.paymentContainer}>
          <View style={styles.cardContainer}>
            <Text style={styles.label}>Card Number: </Text>
            <TextInput
              style={styles.input}
              placeholder="Enter your card number"
              value={cardNumber}
              onChangeText={setCardNumber}
              keyboardType="numeric"
            />
          </View>
          {cardNumberError ? <Text style={styles.error}>{cardNumberError}</Text> : null}

          <View style={styles.numberContainer}>
            <Text style={styles.label}>Expiry Date:</Text>
            <TextInput
              style={styles.inputDate}
              placeholder="Enter your expiry date"
              value={expiryDate}
              onChangeText={setExpiryDate}
              keyboardType="numeric"
            />
            {expiryDateError ? <Text style={styles.error}>{expiryDateError}</Text> : null}

            <Text style={styles.labelCVV}>CVV:</Text>
            <TextInput
              style={styles.inputCVV}
              placeholder="Enter your CVV"
              value={cvv}
              onChangeText={setCvv}
              keyboardType="numeric"
              secureTextEntry
            />
            {cvvError ? <Text style={styles.error}>{cvvError}</Text> : null}
          </View>

          <View style={styles.nameContainer}>
            <Text style={styles.label}>Name on Card:</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter your card name"
              value={name}
              onChangeText={setName}
            />
            {nameError ? <Text style={styles.error}>{nameError}</Text> : null}
          </View>
        </View>
        <Image source={AiBook} style={styles.AiBookImg} />
        <Text style={styles.pricetag}>$12.99</Text>

        <TouchableOpacity style={styles.buyButtonIcon} onPress={handleConfirmPurchase}>
          <Image source={ConfirmPurchaseButton} style={styles.ConfirmPurchaseButton} />
        </TouchableOpacity>
      </View>

      <TouchableOpacity style={styles.goBackIcon} onPress={handleGoBack}>
        <Image source={goBackButton} style={styles.goBack} />
      </TouchableOpacity>
    </KeyboardAvoidingView>
  );
};

const styles = StyleSheet.create({
  container: 
  {
    flex: 1,
    backgroundColor: '#E0F6FF',
    alignItems: "center",
    justifyContent: "center",
  },
  paymentContainer:
  {
    width: '50%',
  },
  cardContainer:
  {
    width: '100%',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 40,
    left: 60,
    top: -30,
  },
  numberContainer:
  {
    width: '100%',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    left: 60,
    top: -10,
  },
  nameContainer:
  {
    width: '100%',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 40,
    left: 60,
    top: -10,
  },
  label: {
    marginRight: 250,
    bottom: 20,
    fontSize: 30,
    fontFamily: "Itim_400Regular",
  },
  labelCVV: 
  {
    marginRight: 20,
    right: 123,
    fontSize: 30,
    fontFamily: "Itim_400Regular",
  },
  input:
  {
    backgroundColor: '#FFFFFF',
    paddingHorizontal: 10, // Reduced padding
    paddingVertical: 5,
    width: 525,
    height: 50, // Increased height
    borderRadius: 14,
    right: 230,
    bottom: 15,
    fontSize: 18, // Adjust font size if needed
  },
  inputDate:
  {
    backgroundColor: '#FFFFFF',
    paddingHorizontal: 10, // Reduced padding
    paddingVertical: 5,
    right: 190,
    borderRadius: 14,
    width: 183,
    height: 50, // Increased height
    bottom: 5,
    fontSize: 18, // Adjust font size if needed
  },
  inputCVV:
  {
    backgroundColor: '#FFFFFF',
    width: 183,
    height: 50, // Increased height
    borderRadius: 14,
    paddingHorizontal: 10, // Reduced padding
    paddingVertical: 5,
    right: 120,
    fontSize: 18, // Adjust font size if needed
  },
  header:
  {
    fontSize: 50,
    fontFamily: "Itim_400Regular",
    color: '#3F3CB4',
    top: 125,
    left: 5,
  },
  AiBookTitle:
  {
    fontSize: 40,
    fontFamily: "Itim_400Regular",
    color: '#ED5D5B',
    bottom: 45,
    left: 60,
    width: 550,
    height: 48,
  },
  AiBookImg:
  {
    left: 865,
    bottom: 275,
    width: 254,
    height: 254,
  },
  pricetag:
  {
    fontSize: 30,
    fontFamily: "Itim_400Regular",
    color: '#3D3AAF',
    width: 84,
    height: 36,
    bottom: 260,
    left: 950,
  },
  ConfirmPurchaseButton:
  {
    bottom: 70,
    left: 840,
  },
  backgroundImage: 
  {
    flex: 1,
    justifyContent: 'center',
    height: '100%',
    marginTop: 10,
    marginBottom: -600,
  },
  goBackIcon:
  {
    color: '#292D32',
    backgroundColor: 'transparent',
    bottom: 800,
    right: 535,
    zIndex: 1,
  },
  goBack:
  {
    width: 77,
    height: 77,
  },


  error: {
    color: 'red',
    marginTop: 10,
  },
});

export default Payment;
