import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Animated, Easing } from 'react-native';

const Page2 = () => {
  const [rotation] = useState(new Animated.Value(0));

  useEffect(() => {
    const animation = Animated.loop(
      Animated.timing(rotation, {
        toValue: 1,
        duration: 20000,
        easing: Easing.linear,
        useNativeDriver: true,
      })
    );
    animation.start();
    return () => animation.stop(); // Clean up animation on unmount
  }, [rotation]);

  const rotateInterpolation = rotation.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  return (
    <View style={styles.sunlightContent}>
      <View style={styles.sun}>
        <View style={[styles.triangle, { top: 15, left: 15 }]} />
        <View style={[styles.triangle, { top: 15, right: 15 }]} />
        <View style={[styles.triangle, { bottom: 15, left: 15 }]} />
        <View style={[styles.triangle, { bottom: 15, right: 15 }]} />
        <View style={styles.ball} />
      </View>
      <Text style={styles.footer}>This ball has <Text style={styles.energy}>energy</Text>.</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  sunlightContent: {
    flex: 1,
    backgroundColor: '#1e1919',
    justifyContent: 'center',
    alignItems: 'center',
  },
  sun: {
    position: 'relative',
    backgroundColor: '#fcc952',
    height: 170,
    width: 170,
    borderRadius: 85,
    borderWidth: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  ball: {
    backgroundColor: '#ff1a66',
    height: 150,
    width: 150,
    borderRadius: 75,
    borderWidth: 1,
    zIndex: 1,
    position: 'absolute',
  },
  footer: {
    position: 'absolute',
    bottom: 20,
    fontSize: 20,
    color: 'white',
  },
  energy: {
    color: 'yellow',
  },
  triangle: {
    position: 'absolute',
    width: 0,
    height: 0,
    borderTopWidth: 60,
    borderRightWidth: 60,
    borderBottomWidth: 0,
    borderLeftWidth: 0,
    borderTopColor: 'yellow',
  },
});

export default Page2;
