import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated } from 'react-native';

export default function page2() {
  const [popped, setPopped] = useState(false);
  const scaleValue = new Animated.Value(1);

  const handlePress = () => {
    setPopped(true);
    Animated.timing(scaleValue, {
      toValue: 1.2,
      duration: 5000, // 5 seconds
      useNativeDriver: true,
    }).start(() => {
      setPopped(false);
      scaleValue.setValue(1);
    });
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={handlePress} activeOpacity={0.8}>
        <Animated.View style={[styles.sunlightContent, popped && { transform: [{ scale: scaleValue }] }]}>
          <View style={styles.sun}>
            <View style={styles.ball} />
            <View style={styles.shadow} />
            <Text style={styles.box}></Text>
          </View>
          <View style={styles.sunlightBox}>
            <View style={styles.sunlightBoxSpan}></View>
            <View style={styles.sunlightBoxSpan}></View>
            <View style={styles.sunlightBoxSpan}></View>
          </View>
        </Animated.View>
      </TouchableOpacity>
      <View style={styles.footer}>
        <Text style={styles.energy}>This ball has energy.</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 0,
    margin: 0,
    backgroundColor: '#1e1919',
    overflow: 'hidden',
    fontFamily: 'Nunito Sans',
    fontWeight: 'bold',
  },
  sun: {
    margin: 0,
    backgroundColor: '#fcc952',
    height: 170,
    width: 170,
    borderRadius: 100,
    borderWidth: 1,
  },
  ball: {
    backgroundColor: '#ff1a66',
    height: 150,
    width: 150,
    borderRadius: 75,
    borderWidth: 1,
    position: 'absolute',
    top: '4%',
    left: '4%',
    zIndex: 1,
  },
  sunlightContent: {
    bottom: '0%',
    left: '50%',
    position: 'absolute',
    right: 0,
    top: '50%',
    transform: [{ translateX: '-50%' }, { translateY: '-50%' }],
    width: 'max-content',
    height: 'max-content',
  },
  shadow: {
    backgroundColor: 'rgba(8, 8, 8, 1)',
    height: 40,
    width: 105,
    borderRadius: 50,
    position: 'absolute',
    top: 60,
    left: 30,
    zIndex: -1,
    animation: 'shrink 0.9s infinite',
  },
  footer: {
    position: 'absolute',
    top: '80%',
    left: '70%',
    transform: [{ translateX: '-50%' }, { translateY: '-50%' }],
    fontSize: 60,
    zIndex: 10,
    height: 'auto',
    width: '100%',
  },
  energy: {
    color: 'yellow',
  },
  sunlightBox: {
    filter: 'drop-shadow(0px 0px 12px rgba(252, 291, 82, 0.8))',
    height: '100%',
    width: '100%',
    position: 'absolute',
    top: 0,
    left: 0,
    animation: 'Rotate 20s linear infinite',
  },
  sunlightBoxSpan: {
    height: 170,
    width: 170,
    backgroundColor: '#f8a037',
    position: 'absolute',
    top: 0,
    left: 0,
    zIndex: 1,
  },
});
