import React, { useState } from 'react';
import { View, StyleSheet, Animated, Easing, TouchableOpacity, Text } from 'react-native';

const Page3 = () => {
  const [bounceValue] = useState(new Animated.Value(0));
  const [isAnimating, setIsAnimating] = useState(false);

  const startAnimation = () => {
    setIsAnimating(true);
    let bounceCount = 0;

    const bounce = () => {
      Animated.sequence([
        Animated.timing(bounceValue, {
          toValue: 1,
          duration: 500,
          easing: Easing.bounce,
          useNativeDriver: true,
        }),
        Animated.timing(bounceValue, {
          toValue: 0,
          duration: 500,
          easing: Easing.bounce,
          useNativeDriver: true,
        }),
      ]).start(() => {
        bounceCount++;
        if (bounceCount < 6) {
          bounce();
        } else {
          setIsAnimating(false);
        }
      });
    };

    bounce();
  };

  const stopAnimation = () => {
    setIsAnimating(false);
    bounceValue.stopAnimation();
  };

  const translateY = bounceValue.interpolate({
    inputRange: [0, 0.17, 0.33, 0.5, 0.67, 0.83, 1],
    outputRange: [0, -30, 60, -30, 90, -30, 120], // Vertical movement for 6 steps
  });

  const translateX = bounceValue.interpolate({
    inputRange: [0, 0.17, 0.33, 0.5, 0.67, 0.83, 1],
    outputRange: [0, 20, 40, 60, 80, 100, 120], // Horizontal movement for 6 steps
  });

  const ballStyle = {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: 'blue',
    position: 'absolute',
    top: -120,
    left: -30,
    transform: [
      { translateY },
      { translateX },
    ],
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={isAnimating ? stopAnimation : startAnimation}>
        <Animated.View style={ballStyle} />
      </TouchableOpacity>
      <Text style={{ color: 'black', fontSize: 50 }}>This is a ball</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
    position: 'relative',
  },
});

export default Page3;
