import React, { useState } from 'react';
import { View, TouchableOpacity, Text, StyleSheet, Image } from 'react-native';
import toggleButtonImage from '../assets/menuImages/toggleButton.png';
import userIconImage from '../assets/menuImages/userIcon.png';
import parentsIconImage from '../assets/menuImages/parents.png';
import kidIconImage from '../assets/menuImages/kidIcon.png';
import settingsIconImage from '../assets/menuImages/settings.png';
import logoutIconImage from '../assets/menuImages/logoutIcon.png';

const MenuButton = ({ userMode, onOptionPress }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option) => {
    // onOptionPress(option);
    // toggleMenu();
    // handle the option that is clicked
    if (userMode === 'parents') {
      if (option === 'Switch to parents mode') {
        console.log(option);
        // navigate to the parent view page
      } else if (option === 'Settings') {
        console.log(option);
        // navigate to the setting view page
      }
    } else {
      if (option === 'Switch to Kids mode') {
        console.log(option);
        // switch to kids bedroom view page
      } else if (option === 'Settings') {
        console.log(option);
        // switch to settings view page
      } else if (option === 'Logout') {
        console.log(option);
        // logout the user
      }
    }
  };

  // options for parents mode
  const parentOptions = [
    { label: 'UserName', image: userIconImage },
    { label: 'Switch to parents mode', image: parentsIconImage },
    { label: 'Settings', image: settingsIconImage },
  ];

  // options for kids mode
  const kidsOptions = [
    { label: 'UserName', image: userIconImage },
    { label: 'Switch to Kids mode', image: kidIconImage },
    { label: 'Settings', image: settingsIconImage },
    { label: 'Logout', image: logoutIconImage },
  ];

  const options = userMode === 'parents' ? parentOptions : kidsOptions;

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={toggleMenu} style={styles.toggleButton}>
        <Image source={toggleButtonImage} style={styles.toggleImage} />
      </TouchableOpacity>

      {/* check if the toggle button is clicked  */}

      {isOpen && (
        // if clicked display the options
        <View style={styles.optionsContainer}>
          {options.map((option, index) => (
            <TouchableOpacity
              key={index}
              style={[
                styles.option,
                index === options.length - 1 && { borderBottomWidth: 0 },
              ]}
              onPress={() => handleOptionClick(option.label)}
            >
              <View style={styles.imageContainer}>
                <Image source={option.image} style={styles.optionImage} />
              </View>
              <Text>{option.label}</Text>
            </TouchableOpacity>
          ))}
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 35,
    left: 30,
    zIndex: 1,
  },
  toggleButton: {
    backgroundColor: 'transparent',
    borderRadius: 5,
  },
  toggleImage: {
    width: 41,
    height: 31,
  },
  optionsContainer: {
    marginTop: 5,
    backgroundColor: 'lightgray',
    borderRadius: 15,
    marginLeft: 25,
  },
  option: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingVertical: 10,
    paddingHorizontal: 10,
    borderBottomWidth: 1,
    borderBottomColor: 'gray',
    paddingRight: 20,
  },
  imageContainer: {
    width: 47,
    height: 44,
    marginRight: 10,
  },
  optionImage: {
    flex: 1,
    width: undefined,
    height: undefined,
    resizeMode: 'contain',
  },
});

export default MenuButton;
