import React from 'react';
import { View, Animated, StyleSheet } from 'react-native';

export default function App() {
  const ballHeight = new Animated.Value(50);
  const ballWidth = new Animated.Value(50);
  const translateY = new Animated.Value(0);

  React.useEffect(() => {
    animateBall();
  }, []);

  const animateBall = () => {
    Animated.sequence([
      Animated.timing(ballHeight, {
        toValue: 50,
        duration: 90,
        useNativeDriver: false,
      }),
      Animated.timing(ballWidth, {
        toValue: 40,
        duration: 90,
        useNativeDriver: false,
      }),
      Animated.timing(translateY, {
        toValue: 240,
        duration: 450,
        useNativeDriver: false,
      }),
      Animated.timing(ballWidth, {
        toValue: 57,
        duration: 300,
        useNativeDriver: false,
      }),
      Animated.timing(ballHeight, {
        toValue: 30,
        duration: 300,
        useNativeDriver: false,
      }),
      Animated.timing(translateY, {
        toValue: 0,
        duration: 600,
        useNativeDriver: false,
      }),
    ]).start(() => {
      animateBall();
    });
  };

  return (
    <View style={styles.container}>
      <View style={styles.container}>
        <Animated.View
          style={[
            styles.ball,
            {
              height: ballHeight,
              width: ballWidth,
              transform: [{ translateY }],
            },
          ]}
        />
      </View>
      <View style={styles.shadow} />
      <View style={styles.footer}>
        <Text style={{ fontSize: 60 }}>This is a ball.</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    height: 500,
    width: 500,
    position: 'absolute',
    margin: 'auto',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: '#1e1919',
    fontFamily: 'Nunito Sans',
    fontWeight: 'bold',
  },
  ball: {
    backgroundColor: '#ff1a66',
    borderRadius: 50,
    borderWidth: 1,
    position: 'relative',
    left: 220,
  },
  shadow: {
    backgroundColor: 'rgba(8, 8, 8, 1)',
    height: 10,
    width: 65,
    borderRadius: 50,
    position: 'relative',
    top: 310,
    left: 212,
    zIndex: -1,
  },
  footer: {
    position: 'absolute',
    top: '80%',
    left: '80%',
    transform: [{ translateX: -50 }, { translateY: -50 }],
    fontSize: 60,
    zIndex: 10,
    height: 'auto',
    width: '100%',
  },
});
