import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Animated, Easing } from 'react-native';

const Sunlight = () => {
  const [rotation] = useState(new Animated.Value(0));

  useEffect(() => {
    const animation = Animated.loop(
      Animated.timing(rotation, {
        toValue: 1,
        duration: 20000,
        easing: Easing.linear,
        useNativeDriver: true,
      })
    );
    animation.start();
    return () => animation.stop(); // Clean up animation on unmount
  }, [rotation]);

  const rotateInterpolation = rotation.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  return (
    <View style={styles.sunlightContent}>
      <View style={styles.sun}>
        <View style={styles.ball} />
        <View style={styles.shadow} />
        <View style={styles.sunlightBox}>
          <View style={[styles.light, { transform: [{ rotate: '30deg' }] }]} />
          <View style={[styles.light, { transform: [{ rotate: '60deg' }] }]} />
          <View style={[styles.light, { transform: [{ rotate: '90deg' }] }]} />
        </View>
      </View>
      <Text style={styles.footer}>This ball has <Text style={styles.energy}>energy</Text>.</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  sunlightContent: {
    flex: 1,
    backgroundColor: '#1e1919',
    justifyContent: 'center',
    alignItems: 'center',
  },
  sun: {
    backgroundColor: '#fcc952',
    height: 170,
    width: 170,
    borderRadius: 85,
    borderWidth: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  ball: {
    backgroundColor: '#ff1a66',
    height: 150,
    width: 150,
    borderRadius: 75,
    borderWidth: 1,
    position: 'absolute',
    zIndex: 1,
    top: '4%',
    left: '4%',
  },
  shadow: {
    backgroundColor: 'rgba(8, 8, 8, 1)',
    height: 40,
    width: 105,
    borderRadius: 50,
    position: 'absolute',
    zIndex: -1,
    top: 60,
    left: 30,
    transform: [{ scale: 0.9 }], // Use transform instead of 'animation' property
  },
  sunlightBox: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    justifyContent: 'center',
    alignItems: 'center',
    transform: [{ rotate: rotateInterpolation }],
    shadowColor: '#fc9352',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.8,
    shadowRadius: 12,
  },
  light: {
    backgroundColor: '#f8a037',
    height: 170,
    width: 170,
    borderRadius: 85,
    position: 'absolute',
    zIndex: 1,
  },
  footer: {
    position: 'absolute',
    bottom: 20,
    fontSize: 20,
    color: 'white',
  },
  energy: {
    color: 'yellow',
  },
});

export default Sunlight;
