import React from 'react';
import { View, Text, StyleSheet, Animated, Easing } from 'react-native';

const BallAnimation = () => {
  const animatedValue = new Animated.Value(0);

  const startAnimation = () => {
    Animated.timing(animatedValue, {
      toValue: 1,
      duration: 900,
      easing: Easing.linear,
      useNativeDriver: true,
    }).start(() => {
      animatedValue.setValue(0);
      startAnimation();
    });
  };

  startAnimation();

  const containerStyle = {
    height: 500,
    width: 500,
    position: 'absolute',
    margin: 'auto',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: '#1e1919',
    fontFamily: 'Nunito Sans',
    fontWeight: 'bold',
  };

  const ballStyle = {
    backgroundColor: '#ff1a66',
    height: 50,
    width: 50,
    borderRadius: 25,
    position: 'relative',
    top: animatedValue.interpolate({
      inputRange: [0, 0.1, 0.3, 0.5, 0.75, 1],
      outputRange: [100, 100, 100, 340, 340, 0],
    }),
    left: animatedValue.interpolate({
      inputRange: [0, 0.5, 1],
      outputRange: [220, 220, 220],
    }),
  };

  const shadowStyle = {
    backgroundColor: 'rgba(8, 8, 8, 1)',
    height: 10,
    width: 65,
    borderRadius: 50,
    position: 'relative',
    top: 310,
    left: 212,
    transform: [
      {
        scaleX: animatedValue.interpolate({
          inputRange: [0, 0.5, 1],
          outputRange: [1, 0.5, 1],
        }),
      },
    ],
  };

  return (
    <View style={containerStyle}>
      <View style={styles.container}>
        <Animated.View style={[ballStyle, styles.ball]} />
      </View>
      <Animated.View style={[shadowStyle, styles.shadow]} />
      <Text style={styles.footer}>This is a ball.</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: 57,
    height: 50,
  },
  ball: {
    position: 'absolute',
    zIndex: 1,
  },
  shadow: {
    position: 'absolute',
    zIndex: -1,
  },
  footer: {
    position: 'absolute',
    top: '80%',
    left: '80%',
    transform: [{ translateX: -50 }, { translateY: -50 }],
    fontSize: 60,
    zIndex: 10,
    height: 'auto',
    width: '100%',
    color: 'white', // Added for visibility
  },
});

export default BallAnimation;
