import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import {auth,db} from './firebase'; // Adjust the path based on your firebase setup

const PinEntryScreen = ({ navigation }) => {
  const [pin, setPin] = useState('');
  const [error, setError] = useState('');

  const handlePinChange = (enteredPin) => {
    setPin(enteredPin);
  };

  const handlePinSubmit = () => {
    // Retrieve the logged-in user's email from your authentication state
    const userEmail = firebase.auth().currentUser.email;

    // Retrieve the user's PIN from the database
    const userRef = firebase.firestore().collection('users').doc(userEmail);
    userRef.get().then((doc) => {
      if (doc.exists) {
        const userData = doc.data();
        const userPin = userData.pin;

        if (pin === userPin) {
          // PIN matches, navigate to payment page
          navigation.navigate('PaymentPage');
        } else {
          // PIN does not match, display error message
          setError('Incorrect PIN. Please try again.');
        }
      } else {
        console.log('User not found');
        setError('User not found.');
      }
    });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.errorText}>{error}</Text>
      <Text style={styles.prompt}>Enter 4-Digit PIN:</Text>
      <TextInput
        style={styles.input}
        value={pin}
        onChangeText={handlePinChange}
        keyboardType="numeric"
        maxLength={4}
      />
      <Button title="Submit" onPress={handlePinSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  errorText: {
    color: 'red',
    marginBottom: 10,
  },
  prompt: {
    fontSize: 18,
    marginBottom: 20,
  },
  input: {
    width: '80%',
    padding: 10,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 20,
    textAlign: 'center',
    fontSize: 24,
  },
});

export default PinEntryScreen;
