const Page3 = () => {
    const [bounceValue] = useState(new Animated.Value(0));
    const [isAnimating, setIsAnimating] = useState(false);
  
    const startAnimation = () => {
      setIsAnimating(true);
      let bounceCount = 0;
  
      const bounce = () => {
        Animated.sequence([
          Animated.timing(bounceValue, {
            toValue: 1,
            duration: 1000,
            easing: Easing.linear,
            useNativeDriver: true,
          }),
          Animated.timing(bounceValue, {
            toValue: 0,
            duration: 1000,
            easing: Easing.linear,
            useNativeDriver: true,
          }),
        ]).start(() => {
          bounceCount++;
          if (bounceCount < 6) {
            bounce();
          } else {
            setIsAnimating(false);
            bounceValue.stopAnimation();
          }
        });
      };
  
      bounce();
    };
  
    const ballStyle = {
      width: 50,
      height: 50,
      borderRadius: 25,
      backgroundColor: 'blue',
      transform: [
        {
          translateX: bounceValue.interpolate({
            inputRange: [0, 1],
            outputRange: [-100, 100], // Adjusted outputRange for sideways bounce
          }),
        },
      ],
    };
  
    return (
      <View style={styles.container}>
        <TouchableOpacity onPress={isAnimating ? stopAnimation : startAnimation}>
          <Animated.View style={ballStyle} />
        </TouchableOpacity>
        <Text style={{ color: 'black', fontSize: 50 }}>This ball has energy</Text>
      </View>
    );
  };
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'white',
    },
  
    text: {
      position: 'absolute',
      zIndex: 1, // Ensure the text is behind the ball
      transform: [{ translateY: 1000 }], // Move the text below the screen
      bottom: 0,
      fontWeight: '700',
    },
  });
  
  export default Page3;
