import React from 'react';
import { View, Text, StyleSheet, Animated } from 'react-native';

const NeutronAnimation = () => {
  const outerCircleOpacity = new Animated.Value(0);
  const secondCircleOpacity = new Animated.Value(0);
  const innerCircleOpacity = new Animated.Value(0);

  Animated.loop(
    Animated.sequence([
      Animated.timing(outerCircleOpacity, {
        toValue: 1,
        duration: 4000,
        useNativeDriver: true,
      }),
      Animated.timing(secondCircleOpacity, {
        toValue: 1,
        duration: 5000,
        useNativeDriver: true,
      }),
      Animated.timing(innerCircleOpacity, {
        toValue: 1,
        duration: 6000,
        useNativeDriver: true,
      }),
      Animated.timing(outerCircleOpacity, {
        toValue: 0,
        duration: 0,
        useNativeDriver: true,
      }),
      Animated.timing(secondCircleOpacity, {
        toValue: 0,
        duration: 0,
        useNativeDriver: true,
      }),
      Animated.timing(innerCircleOpacity, {
        toValue: 0,
        duration: 0,
        useNativeDriver: true,
      }),
    ]),
    { iterations: -1 }
  ).start();

  return (
    <View style={styles.container}>
      <View style={styles.nucleus}>
        <View style={styles.row}>
          <View style={styles.proton}></View>
          <View style={styles.proton}></View>
          <View style={styles.neutron}></View>
          <View style={styles.neutron}></View>
        </View>
        <View style={styles.row}>
          <View style={styles.neutron}></View>
          <View style={styles.neutron}></View>
          <View style={styles.proton}></View>
          <View style={styles.proton}></View>
        </View>
      </View>
      <Animated.View style={[styles.circle, { opacity: outerCircleOpacity }]}></Animated.View>
      <Animated.View style={[styles.circle, styles.secondCircle, { opacity: secondCircleOpacity }]}></Animated.View>
      <Animated.View style={[styles.circle, styles.innerCircle, { opacity: innerCircleOpacity }]}></Animated.View>
      <View style={styles.footer}>
        <Text style={styles.neutronWording}>Here are neutrons.</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
    alignItems: 'center',
    justifyContent: 'center',
    perspective: 100,
  },
  nucleus: {
    width: 50,
    height: 50,
    flexDirection: 'row',
    transformStyle: 'preserve-3d',
  },
  row: {
    flex: 1,
    justifyContent: 'space-around',
    alignItems: 'center',
    transform: [{ rotateX: '80deg' }, { rotateY: '180deg' }],
  },
  proton: {
    width: 10,
    height: 10,
    borderRadius: 5,
    backgroundColor: 'red',
  },
  neutron: {
    width: 10,
    height: 10,
    borderRadius: 5,
    backgroundColor: 'blue',
  },
  circle: {
    width: 30,
    height: 30,
    borderRadius: 15,
    borderWidth: 2,
    borderColor: '#ccc',
    position: 'absolute',
  },
  secondCircle: {
    width: 22,
    height: 22,
    borderWidth: 2,
    borderColor: '#ccc',
    top: 40,
    left: 40,
  },
  innerCircle: {
    width: 14,
    height: 14,
    borderWidth: 2,
    borderColor: '#ccc',
    top: 80,
    left: 80,
  },
  footer: {
    position: 'absolute',
    bottom: 20,
  },
  neutronWording: {
    color: 'blue',
    fontSize: 18,
  },
});

export default NeutronAnimation;
