import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';

const PinEntryScreen = ({ navigation }) => {
  const [pin, setPin] = useState('');

  const handlePinChange = (enteredPin) => {
    setPin(enteredPin);
  };

  const handlePinSubmit = () => {
    if (pin.length === 4) {
      // Handle PIN verification logic here
      console.log('PIN entered:', pin);
      navigation.goBack(); // Navigate back or to another screen after PIN verification
    } else {
      console.log('Please enter a 4-digit PIN');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.prompt}>Enter 4-Digit PIN:</Text>
      <TextInput
        style={styles.input}
        value={pin}
        onChangeText={handlePinChange}
        keyboardType="numeric"
        maxLength={4}
      />
      <Button title="Submit" onPress={handlePinSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  prompt: {
    fontSize: 18,
    marginBottom: 20,
  },
  input: {
    width: '80%',
    padding: 10,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 20,
    textAlign: 'center',
    fontSize: 24,
  },
});

export default PinEntryScreen;
