export function validateEmail(email) {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

export function validateName(name) {
  const nameRegex = /^[a-zA-Z]+$/;
  return nameRegex.test(name);
}

export function validateDOB(DOB) {
  const dobRegex = /^(0[1-9]|[12][0-9]|3[01])\/(0[1-9]|1[0-2])\/\d{4}$/;
  
  if (!dobRegex.test(DOB)) {
    if (!/^(\d{1,2})\/(\d{1,2})\/(\d{4})$/.test(DOB)) {
      return "Invalid date format. Please use dd/mm/yyyy format.";
    } else {
      return "Invalid date. Please enter a valid date.";
    }
  }
  
  return null;
}

export function validateParentalPin(parentalPin) {
  const pinRegex = /^\d{4}$/;
  return pinRegex.test(parentalPin);
}
export function validatePassword(password) {
  // Ensure password is at least 6 characters long
  if (password.length < 6) {
    return false;
  }
  const capitalRegex = /[A-Z]/;
  if (!capitalRegex.test(password)) {
    return false;
  }
  const specialRegex = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+/;
  if (!specialRegex.test(password)) {
    return false;
  }

  const numberRegex = /[0-9]/;
  if (!numberRegex.test(password)) {
    return false;
  }

  return true;
}
