import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Animated, Easing } from 'react-native';

const Page2 = () => {
  const [rotation] = useState(new Animated.Value(0));

  useEffect(() => {
    const animation = Animated.loop(
      Animated.timing(rotation, {
        toValue: 1,
        duration: 20000,
        easing: Easing.linear,
        useNativeDriver: true,
      })
    );
    animation.start();
    return () => animation.stop(); // Clean up animation on unmount
  }, [rotation]);

  const rotateInterpolation = rotation.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  const renderTriangles = () => {
    const numberOfTriangles = 4;
    const triangles = [];
    const radius = 75; // Radius of the circle
    const angleIncrement = (2 * Math.PI) / numberOfTriangles;
  
    for (let i = 0; i < numberOfTriangles; i++) {
      const angle = angleIncrement * i;
      const x = Math.cos(angle) * radius + 75; // Adjusted for the ball's position
      const y = Math.sin(angle) * radius + 75; // Adjusted for the ball's position
  
      triangles.push(
        <View
          key={i}
          style={[
            styles.triangle,
            { top: y - 30, left: x - 30, transform: [{ rotate: `${angle}rad` }] },
          ]}
        />
      );
    }
  
    return triangles;
  };
  

  return (
    <View style={styles.sunlightContent}>
      <View style={styles.sun}>
        {renderTriangles()}
        <View style={styles.ball} />
      </View>
      <Text style={styles.footer}>This ball has <Text style={styles.energy}>energy</Text>.</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  sunlightContent: {
    flex: 1,
    backgroundColor: '#1e1919',
    justifyContent: 'center',
    alignItems: 'center',
  },
  
  ball: {
    backgroundColor: '#ff1a66',
    height: 150,
    width: 150,
    borderRadius: 75,
    borderWidth: 1,
    zIndex: 1,
    position: 'absolute',
  },
  footer: {
    position: 'absolute',
    bottom: 20,
    fontSize: 20,
    color: 'white',
  },
  energy: {
    color: 'yellow',
  },
  triangle: {
    position: 'absolute',
    width: 0,
    height: 0,
    borderTopWidth: 60,
    borderRightWidth: 60,
    borderBottomWidth: 0,
    borderLeftWidth: 0,
    borderTopColor: 'yellow',
    transformOrigin: 'center center',
  },
});

export default Page2;
