import React, { useState } from 'react';
import { View, Button, StyleSheet } from 'react-native';
import Page0 from './page0-5/page0';
import Page1 from './page0-5/page1';
import Page2 from './page0-5/page2';
import Page3 from './page0-5/page3';
import Page4 from './page0-5/page4';

const App = () => {
  const [currentPage, setCurrentPage] = useState(0);

  const handleNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const handleBack = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  return (
    <View style={styles.container}>
      {currentPage === 0 && <Page0 />}
      {currentPage === 1 && <Page1 />}
      {currentPage === 2 && <Page2 />}
      {currentPage === 3 && <Page3 />}
      {currentPage === 4 && <Page4 />}
      
      <View style={styles.navigation}>
        {currentPage > 0 && <Button title="Back" onPress={handleBack} />}
        {currentPage < 4 && <Button title="Next" onPress={handleNext} />}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  navigation: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 20,
  },
});

export default App;
